, r;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (cin &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ll output = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    output = &lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(&lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(&lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(table[r].ACMnum, table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].ACMnum), &lt;span class=&#34;built_in&#34;&gt;modMul&lt;/span&gt;(table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].ACnum, &lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(table[r].Mnum, table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].Mnum))), &lt;span class=&#34;built_in&#34;&gt;modMul&lt;/span&gt;(table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].Anum, &lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(&lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(table[r].CMnum, table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].CMnum), &lt;span class=&#34;built_in&#34;&gt;modMul&lt;/span&gt;(table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].Cnum, &lt;span class=&#34;built_in&#34;&gt;modSub&lt;/span&gt;(table[r].Mnum, table[l&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;].Mnum)))));&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; output &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
